import React, { useState, useEffect } from 'react';
import Logo from './Assets/doggo.jpg';
import './Styles/Home.scss'
import axios from 'axios';
import List from './List';

function Home(props){

	const [ data, setData ] = useState();
	const [searchTerm, setSearchTerm] = useState();
	const [results,setResults] = useState([]);
	const [error, setError]= useState([]);


	useEffect(() => {
    const getDogs = async () => {
        try {
            let mounted = true;
            if(mounted){
              const res = await axios.get('http://localhost:3000/dogs/');
							setData(res.data.message);
							setResults(Object.keys(data));
            }
        } catch (err) {

        }
				console.log('fire');
    }
    getDogs();
	},[results]);

	let myKeys;
	if(data){
		myKeys = Object.keys(data);
		// console.log(myKeys);
	}

	let classN = "hidden";

	function Search(evt){
		setSearchTerm(evt.target.value);
	}


	if(searchTerm){
		console.log('here')
		console.log("->",searchTerm);
		var arraycontains = (myKeys.indexOf(searchTerm) > -1);
		var index = myKeys.indexOf(searchTerm);
		console.log(index);
		console.log(arraycontains);
		if(arraycontains){
			// set list to resulting item
			console.log('here in contains');
			setResults(myKeys[index]);
			console.log(results);

		} else {
			classN="show";
			setError("No Results Found")
		}
	}

	function submitForm(evt){
		evt.preventDefault();
	}

	return (
		<>
		<div className="col">
		</div>
		<div className="col">
				<div className="container-fluid main">
				<img src={Logo} className="logo-image" alt="..."/>
					<h5>Doggo</h5>

					<form onSubmit={submitForm}>
						<div className="form-group">
							<input className="form-control" id="search"  type="text" name="search" placeholder="Search Breeds" onChange={Search}  />
						</div>
					</form>
					<List items={data}/>
				</div>
		</div>

		<div className="col">

		</div>
		</>
	);
}

export default Home;
