import React, { useState, useEffect } from 'react';
import  { Redirect, useHistory } from 'react-router-dom'
import Logo from './Assets/doggo.jpg';
import './Styles/Home.scss'
import axios from 'axios';
import List from './List';

function Home(props){

	const [ data, setData ] = useState();
	const [search, setSearch] = useState();
	const [results, setResults]= useState([]);


	useEffect(() => {
    const getDogs = async () => {
        try {
            let mounted = true;
            if(mounted){
              const res = await axios.get('http://localhost:3000/dogs/');
							console.log("*****",res);
							setData(res.data);

            }
        } catch (err) {

        }
    }
    getDogs();
	},[search]);

	let myKeys;
	if(data){
		myKeys = Object.keys(data.message);
		console.log(myKeys);
	}

	function Search(evt){
		//filter results based on search Terms
		let searchValue = evt.target.value;
		setSearch(searchValue);
		console.log(search);

		if(search !== "" || search!== undefined){
			var matchingKeys = myKeys.filter(function(key){ return key.indexOf(search) !== -1 });
			console.log("->",matchingKeys);
			if(matchingKeys.length !== 0) {
				let matchingValues = matchingKeys.map(function(key){ return data.message[key] });
				console.log(matchingKeys);
			}
		}



	}

	return (
		<>
		<div className="col">
		</div>
		<div className="col">
				<div className="container-fluid main">
				<img src={Logo} className="logo-image" alt="..."/>
					<h5>Doggo</h5>
					<form>
						<div class="form-group">
							<input className="form-control" id="search"  type="text" name="search" placeholder="Search Breeds" onChange={Search} />
						</div>
					</form>
					<List items={data}/>
				</div>
		</div>

		<div className="col">

		</div>
		</>
	);
}

export default Home;
