				console.log('fire');
    }
    getDogs();
	},[]);

	let myKeys;
	if(data){
		myKeys = Object.keys(data);
		console.log(myKeys);
	}

	let classN = "hidden";
	function Search(evt){
		//filter results based on search Terms
		let searchValue = evt.target.value;
		setSearch(searchValue);
		console.log("->",search);

		if(search !== "" || search!== undefined){
			var arraycontains = (myKeys.indexOf(search) > -1);
			console.log(arraycontains);
			if(arraycontains){
				// set list to resulting item
				setResults(data[search]);
				console.log(results);

			} else {
				classN="show";
				setError("No Results Found")
			}
		}
	}

	function submitForm(evt){
		evt.preventDefault();
	}

	return (
		<>
		<div className="col">
		</div>
		<div className="col">
				<div className="container-fluid main">
				<img src={Logo} className="logo-image" alt="..."/>
					<h5>Doggo</h5>

					<form onSubmit={submitForm}>
						<div className="form-group">
							<input className="form-control" id="search"  type="text" name="search" placeholder="Search Breeds" onChange={Search} />
						</div>
					</form>
					<List items={results}/>
				</div>
		</div>

		<div className="col">

		</div>
		</>
	);
}

export default Home;
