import React, { useState, useEffect } from 'react';
import {Route, Switch} from "react-router-dom";
import { useParams } from "react-router-dom";
import axios from 'axios';


function Detail(){

    const {dog} = useParams();
    const [images, setImages] = useState();

// url to display 4 random pics https://dog.ceo/api/breeds/image/random/4
    useEffect(() => {
        const getDogs = async () => {
                try {
                        let mounted = true;
                        if(mounted){
														if(dog !== ""){
															const res = await axios.get('https://dog.ceo/api/breed/'+dog+'/images/random/4');
															console.log("return", res.data.message);
															setImages(res.data.message);
															console.log(res.data.message);
														}

                        }
                } catch (err) {

                }
                console.log('fire');
        }
        getDogs();
    },[dog]);



	return (
		<>
        DETAIL for {dog}
        <ul>
            {images.map(listitem => (
							<li className="list-group-item"><img src={listitem	}></img></li>
						))}
        </ul>
        <a href="/Home">Back to Search</a>
        </>
	);
}

export default Detail;
